    const ext = extname(referenceImage).toLowerCase();
    let mimeType: string;
    switch (ext) {
      case ".png":
        mimeType = "image/png";
        break;
      case ".jpg":
      case ".jpeg":
        mimeType = "image/jpeg";
        break;
      case ".webp":
        mimeType = "image/webp";
        break;
      default:
        throw new CLIError(
          `Unsupported image format: ${ext}. Supported: .png, .jpg, .jpeg, .webp`
        );
    }

    parts.push({
      inlineData: {
        mimeType,
        data: imageBase64,
      },
    });
  }

  // Add text prompt
  parts.push({ text: prompt });

  const response = await ai.models.generateContent({
    model: "gemini-3-pro-image-preview",
    contents: [{ parts }],
    config: {
      responseModalities: ["TEXT", "IMAGE"],
      imageConfig: {
        aspectRatio: aspectRatio,
        imageSize: size,
      },
    },
  });

  // Extract image data from response
  let imageData: string | undefined;

  if (response.candidates && response.candidates.length > 0) {
    const responseParts = response.candidates[0].content.parts;
    for (const part of responseParts) {
      if (part.inlineData && part.inlineData.data) {
        imageData = part.inlineData.data;
        break;
      }
    }
  }

  if (!imageData) {
    throw new CLIError("No image data returned from Gemini API");
  }

  const imageBuffer = Buffer.from(imageData, "base64");
  await writeFile(output, imageBuffer);
  console.log(`Image saved to ${output}`);
}

// ============================================================================
// Main
// ============================================================================

async function main(): Promise<void> {
  try {
    await loadEnv();
    const args = parseArgs(process.argv);

    // Handle variations
    if (args.variations && args.variations > 1) {
      console.log(`Generating ${args.variations} variations...`);
      const basePath = args.output.replace(/\.png$/, "");
      const promises: Promise<void>[] = [];

      for (let i = 1; i <= args.variations; i++) {
        const varOutput = `${basePath}-v${i}.png`;
        promises.push(
          generateImage(
            args.prompt,
            args.size,
            args.aspectRatio,
            varOutput,
            args.referenceImage
          )
        );
      }

      await Promise.all(promises);
      console.log(`Generated ${args.variations} variations`);
      return;
    }

    // Single image generation
    await generateImage(
      args.prompt,
      args.size,
      args.aspectRatio,
      args.output,
      args.referenceImage
    );
  } catch (error) {
    handleError(error);
  }
}

