function detectLanguage(text: string): "de" | "en" {
  const germanPatterns = [
    /\b(ich|du|er|sie|es|wir|ihr|mich|dich|sich)\b/i,
    /\b(und|oder|aber|wenn|dass|weil|denn|ob)\b/i,
    /\b(ist|sind|war|waren|wird|werden|hat|haben|kann|k√∂nnen)\b/i,
